// productsSlice.js
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchitems = createAsyncThunk('redux/fetchProducts', async ({cat}) => {
  const response = await axios.get('https://newsapi.org/v2/top-headlines/sources?category=${cat}&apiKey=e93a068b072140b9a7e35b56d6127880'); // Replace with your API endpoint
  console.log('response');
  return response.data;
});

const categorySlice = createSlice({
  name: 'items',
  initialState: [],
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchitems.fulfilled, (state, action) => {
        return action.payload;
      });
  },
});

export default categorySlice.reducer;
