// productsSlice.js
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const fetchProducts = createAsyncThunk('redux/fetchProducts', async () => {
  const response = await axios.get('https://newsapi.org/v2/top-headlines?country=us&apiKey=e93a068b072140b9a7e35b56d6127880'); // Replace with your API endpoint
  return response.data;
});

const productsSlice = createSlice({
  name: 'products',
  initialState: [],
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchProducts.fulfilled, (state, action) => {
        console.log('hi');
        return action.payload;
        
      });
  },
});

export default productsSlice.reducer;
